/**
 * retrieves a list of non-responsive classes
 * @param styles            key-value pairs of style definitions
 * @param stylesScssModule  reference to the actual scss module used to retrieve the styles
 */
const getNonResponsiveClasses = (styles, stylesScssModule) => {
  return Object.keys(styles).map(styleName => {
    const regularStyle = styles[styleName];
    if (regularStyle === undefined) {
      return null;
    }
    if (Array.isArray(regularStyle)) {
      return regularStyle.map((key, index) => stylesScssModule[`${styleName}-${index}-${regularStyle[index]}`]);
    }
    return stylesScssModule[`${styleName}-${regularStyle}`];
  });
};
export { getNonResponsiveClasses };